mapper.delete:
UPDATE comment
SET state = 1
WHERE id = #{commentId}

mapper.update:
UPDATE comment
SET body = #{body}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM comment_like
WHERE comment_like.comment_id = #{commentId}

mapper.delete:
DELETE
FROM comment_like
WHERE comment_id = #{commentId}
  AND user_id = #{userId}
